class TaskList extends React.Component{
  constructor(props){
    super(props);

  }
  deleteTask = (index) => {
    let tempList = [];
    tempList = this.props.list;
    tempList.splice(index, 1);
    this.props.deleteTask(tempList);
  };

  render(){
    let task;
    const temp = this.props.templist.map((e, idx) => {
          task = !e.iscomplete ? "complete" : "undo";
          <li key={idx}>
          <span className={task}>{e.task}</span>
          <br />
          <button onClick = {() => this.props.deleteTask(idx)}>Delete</button>
          <hr />
        </li>
      });
    return(<div className = 'list-box'>
            <ul>
            {this.props.templist.map((e, idx) => {
              let task = !e.iscomplete ? "complete" : "undo";
              return (
                <li key={idx}>
                  <span className={task}>{e.task}</span>
                  <br />

                  <button onClick = {() => this.props.deleteTask(idx)}>Delete</button>
                  <hr />
                </li>
              );
            })}
            </ul>
          </div>);
  }
}
export default TaskList;
