import React from "react";


export default class App extends React.Component {
  constructor() {
    super();
    this.state = {
      list: [],
      newtask: "",
      showAll: "all"
    };
  }
  /*Get task from the browser and set it to a newtask  state*/
  inputData = event => this.setState({ newtask: event.target.value });
 /*Get a new task from state and put that one to the list.*/
  addTask = () => {
    let tempList = [];
    tempList = this.state.list;
    let temptask = { task: this.state.newtask, iscomplete: false };
    tempList.push(temptask);
    this.setState({ list: tempList });
    this.setState({ newtask: "" });
  };
  /*Delete task from the list*/
  deleteTask = (givenIndex) => {
    let tempList = [];
    tempList = this.state.list;
    tempList.splice(givenIndex,1);
    this.setState({list:tempList});
  }

  render() {
    const templist =   this.state.list.map((e,idx) => <li key={idx}><span>{e.task}</span><button>completed</button><button onClick={()=>this.deleteTask(idx)}>delete</button></li>);
    return (
      <div className="App">
        <div className="new-task">
          <input onChange={this.inputData} placeholder="enter a new task" />
          <button onClick={this.addTask}>Add </button>
        </div>
        <div className="list-box">
          <ul>
          {templist}
          </ul>
        </div>
      </div>
    );
  }
}
