import React from "react";
import "./css/to-do-list-css.css";

export default class App extends React.Component {
  constructor() {
    super();
    this.state = {
      list: [],
      newtask: "",
      showAll: "all"
    };
  }
  /*Get task from the browser and set it to a newtask  state*/
  inputData = event => this.setState({ newtask: event.target.value });
 /*Get a new task from state and put that one to the list.*/
  addTask = () => {
    let tempList = [];
    tempList = this.state.list;
    let temptask = { task: this.state.newtask, iscomplete: false };
    tempList.push(temptask);
    this.setState({ list: tempList });
    this.setState({ newtask: "" });
  };
  /*Delete task from the list*/
  deleteTask = (givenIndex) => {
    let tempList = [];
    tempList = this.state.list;
    tempList.splice(givenIndex,1);
    this.setState({list:tempList});
  }
  isComplete =  (givenIndex) => {
    let tempList = [];
    tempList = this.state.list;
    tempList[givenIndex].iscomplete = !tempList[givenIndex].iscomplete;
    this.setState({list:tempList});
  }
  render() {

    return (
      <div className="App">
        <div className="new-task">
          <input onChange={this.inputData} placeholder="enter a new task" />
          <button onClick={this.addTask}>Add </button>
        </div>
        <div className="list-box">
          <ul>
          {

            this.state.list.map((e,idx) => {
            let task = !e.iscomplete ? "complete" : "undo";
            console.log(task)
            return(
                  <li key={idx}><span className = {task}>{e.task}</span><br/><hr>
                  <button onClick = {() => this.isComplete(idx)}> {task} </button>
                  <button onClick = {() => this.deleteTask(idx)}> delete </button></hr></li>);
                  })
          }
          </ul>
        </div>
        <div>
          <h3>Show:<button>All</button><button>Active</button><button>Complete</button></h3>
        </div>
      </div>
    );
  }
}
