import React from 'react';
import ReactDOM from 'react-dom';
import "./css/movieStyles.css";


class Movies extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      movies: [],
      title: '',
      error: '',
      page: 1
    }
  }
  getTitle = (event) => this.setState({title:event.target.value});

  getData()
  {

      let url=`http://www.omdbapi.com/?s=${this.state.title}&page=1&apikey=cb289192`
      fetch(url)
      .then(result => result.json())
      .then((data) => this.setState({movies:data}) )
      .catch(console.log)
  }

  render()
  {
    let list = this.state.movies.map((element) => <li> {element.Title} {element.Year}</li>);
    return(
          <div>
            <div>
              <input type='text' placeholder='search' onChange={this.getTitle}/>
              <button onClick={this.getData}>Search</button>
           </div>
           <ul>
           {list}
           </ul>
         </div>)
  }
}
export default Movies;
