import React from 'react';
import ReactDOM from 'react-dom';
import "./css/movieStyles.css";


class Movies extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      movies: [],
      title: '',
      error: '',
      page: 1
    }
  }
  getTitle = (event) => this.setState({title:event.target.value});

  getData (){
      const {title} = this.state;
      const url =  `http://www.omdbapi.com/?s=${title}&page=1&apikey=cb289192`;
      fetch(url)
      .then(result => result.json())
      .then((data) => this.setState({movies:data}) )
      .catch(console.log)
    }

  render()
  {

    return(
          <div>
            <input type='text' placeholder='search' onChange={this.getTitle}/>
            <button onClick={this.getData}>Search</button>
            <ul>
            {
              this.state.movies.map((element) =>
              {

                return (<li> {element.Title} {element.Year}</li>)
              })
            }
            </ul>
         </div>)
  }
}
export default Movies;
