import React from "react";
import "./styles.css";

export default class App extends React.Component{
  constructor(){
    super()
    this.state ={
     list:[

     ],
     newtask:'',
     showAll:'all'



    }
  }
  inputData = (event) =>{ this.setState({newtask:event.target.value})}
  addTask = (givenTask) => {
    const tempList = [];
    tempList = this.state.list;
    let temptask = {newtask:givenTask, complete:false}
    tempList.push(temptask);
    this.setState({list:tempList});
    this.setState({newtask:''})
  }
  render()
  {

    return (
      <div className="App">
      <div className='new-task'>
      <input onKeyUp={this.inputData} placeholder='enter a new task'/>
      <button onClick={this.addTask()}>Add </button>
      </div>
      <div className='list-box'>
        <ul>
        {
          this.state.list.map((e)=><li>{e.newtask}<button>undone</button><button>delete</button></li>)
        }
        </ul>

      </div>

      </div>
    );
  }
}
