import React from 'react';
import ReactDOM from 'react-dom';
import "./css/movieStyles.css";


class Movie extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      rawData: [],
      movies: [],
      title: '',
      error: '',
      page: 1
    }
  }
  handleChange = (e) => {
    console.log(e.target.value)
    this.setState({title: e.target.value});
  }
  handleClick = () => {
    const { title } = this.state;
    const url =  `http://www.omdbapi.com/?s=${title}&page=1&apikey=cb289192`;
    if(title.length){
      fetch(url)
      .then(resp => resp.json())
      .then(data=>
        this.setState({rawData: data})
      )
    } else {
        this.setState({error: 'title should\'nt be empty' });
    }
  }
  render() {

    const { title, error } = this.state;
    return (
      <div className="app">

        <header>
          <h3 id="title-header">Movie Depot</h3>
        </header>

        <section>
            <div className="search-div">
              <input type="text" id="search" onChange={this.handleChange} value={title}/>
              <button onClick={this.handleClick}>Search</button>
            </div>

            <div className="error-display">
              <span>{ error }</span>
            </div>
          </section>

          <section className="container">
            <div className="total-results">
            <p> Total results: <span style={{color: "red"}}>totalResults</span></p>
            </div>
            <div className="movies-container">
              data
            </div>
          </section>

          <div className="load-more">
            <p>show more</p>
        </div>

      </div>
    );
  }
}
export default Movies;
