import React from 'react';
import ReactDOM from 'react-dom';
import "./css/movieStyles.css";


class Movies extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      rawData: [],
      movies: [],
      title: '',
      error: '',
      page: 1
    }
  }
  getTitle = (event) => this.setState({title:event.target.value});

  fetchData = () =>{
      fetch(`http://www.omdbapi.com/?s=${this.state.title}&page=1&apikey=cb289192`)
      .then(result => result.json())
      .then((data) => { this.setState({rawData:data}) })
      .catch(console.log)
    }

  render()
  {
   console.log(this.state.rawData)
    return(
          <div>
            <input type='text' placeholder='search' onKeyUp={this.getTitle}/>
            <button onClick={this.fetchData}>Search</button>
            <ul>
            {
              this.state.rawData.map((element) =>
              {

                return (<li> {element.Title} {element.Year}</li>)
              })
            }
            </ul>
         </div>)
  }
}
export default Movies;
